import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ClimaService } from '../../services/clima.service';

@Component({
  selector: 'app-detalhes-clima',
  templateUrl: './detalhes-clima.page.html',
  styleUrls: ['./detalhes-clima.page.scss'],
  standalone: false
})
export class DetalhesClimaPage implements OnInit {
  // Dados detalhados do clima
  climaDetalhado: any = null;
  // Previsão horária
  previsaoHoraria: any[] = [];
  // Nome da cidade
  cidade: string = '';
  // Controle de erro
  erro: string = '';
  // Controle de loading
  carregando: boolean = false;

  constructor(
    private route: ActivatedRoute,
    private climaService: ClimaService
  ) {}

  ngOnInit() {
    // Recupera e decodifica o parâmetro da rota
    const cidadeEncoded = this.route.snapshot.paramMap.get('cidade') || '';
    this.cidade = decodeURIComponent(cidadeEncoded);
    if (this.cidade) {
      this.carregarDadosDetalhados();
    }
  }

  /**
   * Carrega os dados detalhados do clima
   */
  private carregarDadosDetalhados() {
    this.carregando = true;

    // Busca dados atuais
    this.climaService.getClimaAtual(this.cidade).subscribe({
      next: (data) => {
        this.climaDetalhado = data;
        this.carregarPrevisaoHoraria();
      },
      error: (error) => {
        console.error('Erro ao carregar detalhes:', error);
        this.erro = 'Não foi possível carregar os detalhes do clima';
        this.carregando = false;
      }
    });
  }

  /**
   * Carrega a previsão horária
   */
  private carregarPrevisaoHoraria() {
    this.climaService.getPrevisao5Dias(this.cidade).subscribe({
      next: (data) => {
        // Pega as próximas 24 horas (8 períodos de 3 horas)
        this.previsaoHoraria = data.list.slice(0, 8);
        this.carregando = false;
      },
      error: (error) => {
        console.error('Erro ao carregar previsão horária:', error);
        this.carregando = false;
      }
    });
  }
}
<ion-header [translucent]="true">
  <ion-toolbar color="primary">
    <ion-buttons slot="start">
      <ion-back-button defaultHref="/home"></ion-back-button>
    </ion-buttons>
    <ion-title>Detalhes do Clima</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="ion-padding">
  <!-- Loading indicator -->
  <div class="loading-container" *ngIf="carregando">
    <ion-spinner name="circular"></ion-spinner>
    <p>Carregando detalhes...</p>
  </div>

  <!-- Mensagem de erro -->
  <ion-text color="danger" *ngIf="erro">
    <p class="error-message">{{ erro }}</p>
  </ion-text>

  <!-- Detalhes do clima -->
  <ng-container *ngIf="climaDetalhado && !carregando">
    <!-- Card principal -->
    <ion-card class="clima-detalhado-card" appHighlight>
      <ion-card-header>
        <ion-card-subtitle>Detalhes do clima em</ion-card-subtitle>
        <ion-card-title>{{ climaDetalhado.name }}</ion-card-title>
      </ion-card-header>

      <ion-card-content>
        <div class="clima-info-detalhado">
          <!-- Temperatura e condição atual -->
          <div class="temperatura-principal">
            <h1>{{ climaDetalhado.main.temp | temperatura }}</h1>
            <p>{{ climaDetalhado.weather[0].description | titlecase }}</p>
            <small>Atualizado em: {{ climaDetalhado.dt * 1000 | date:'medium' }}</small>
          </div>

          <!-- Grid de informações detalhadas -->
          <div class="detalhes-grid">
            <div class="detalhe-item">
              <ion-icon name="thermometer-outline"></ion-icon>
              <span>Sensação Térmica</span>
              <strong>{{ climaDetalhado.main.feels_like | temperatura }}</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="water-outline"></ion-icon>
              <span>Umidade</span>
              <strong>{{ climaDetalhado.main.humidity }}%</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="speedometer-outline"></ion-icon>
              <span>Pressão</span>
              <strong>{{ climaDetalhado.main.pressure }} hPa</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="eye-outline"></ion-icon>
              <span>Visibilidade</span>
              <strong>{{ climaDetalhado.visibility / 1000 }} km</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="thermometer-outline"></ion-icon>
              <span>Temp. Máxima</span>
              <strong>{{ climaDetalhado.main.temp_max | temperatura }}</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="thermometer-outline"></ion-icon>
              <span>Temp. Mínima</span>
              <strong>{{ climaDetalhado.main.temp_min | temperatura }}</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="wind-outline"></ion-icon>
              <span>Velocidade do Vento</span>
              <strong>{{ climaDetalhado.wind.speed }} m/s</strong>
            </div>
            <div class="detalhe-item">
              <ion-icon name="compass-outline"></ion-icon>
              <span>Direção do Vento</span>
              <strong>{{ climaDetalhado.wind.deg }}°</strong>
            </div>
          </div>
        </div>
      </ion-card-content>
    </ion-card>

    <!-- Previsão horária -->
    <div class="previsao-horaria" *ngIf="previsaoHoraria.length > 0">
      <h2>Previsão para as próximas horas</h2>
      <swiper-container 
        slides-per-view="auto"
        space-between="10"
        pagination="true"
        keyboard="true"
        mousewheel="true"
        navigation="true"
        class="mySwiper">
        <swiper-slide *ngFor="let previsao of previsaoHoraria">
          <ion-card class="previsao-card" appHighlight>
            <ion-card-content>
              <div class="horario">
                {{ previsao.dt * 1000 | date:'HH:mm' }}
              </div>
              <div class="temperatura">
                {{ previsao.main.temp | temperatura }}
              </div>
              <div class="descricao">
                {{ previsao.weather[0].description }}
              </div>
              <div class="umidade">
                <ion-icon name="water-outline"></ion-icon>
                {{ previsao.main.humidity }}%
              </div>
            </ion-card-content>
          </ion-card>
        </swiper-slide>
      </swiper-container>
    </div>
  </ng-container>
</ion-content>
