import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { HistoricoService } from '../../services/historico.service';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-historico-clima',
  templateUrl: './historico-clima.page.html',
  styleUrls: ['./historico-clima.page.scss'],
})
export class HistoricoClimaPage implements OnInit {
  historicoCidades: string[] = [];
  cidadesPaginadas: string[] = [];
  paginaAtual = 1;
  itensPorPagina = 5;
  totalPaginas = 1;

  constructor(
    private historicoService: HistoricoService,
    private router: Router,
    private alertController: AlertController
  ) {}

  ngOnInit() {
    this.historicoService.getHistorico().subscribe(historico => {
      this.historicoCidades = historico;
      this.totalPaginas = Math.ceil(this.historicoCidades.length / this.itensPorPagina);
      this.atualizarPaginacao();
    });
  }

  atualizarPaginacao() {
    const inicio = (this.paginaAtual - 1) * this.itensPorPagina;
    const fim = inicio + this.itensPorPagina;
    this.cidadesPaginadas = this.historicoCidades.slice(inicio, fim);
  }

  mudarPagina(pagina: number) {
    if (pagina >= 1 && pagina <= this.totalPaginas) {
      this.paginaAtual = pagina;
      this.atualizarPaginacao();
    }
  }

  verDetalhes(cidade: string) {
    const cidadeEncoded = encodeURIComponent(cidade);
    this.router.navigate(['/detalhes-clima', cidadeEncoded]);
  }

  async removerCidade(cidade: string) {
    const alert = await this.alertController.create({
      header: 'Confirmar exclusão',
      message: `Deseja remover ${cidade} do histórico?`,
      buttons: [
        {
          text: 'Cancelar',
          role: 'cancel'
        },
        {
          text: 'Remover',
          handler: () => {
            const index = this.historicoCidades.indexOf(cidade);
            if (index > -1) {
              this.historicoCidades.splice(index, 1);
              this.totalPaginas = Math.ceil(this.historicoCidades.length / this.itensPorPagina);
              if (this.paginaAtual > this.totalPaginas) {
                this.paginaAtual = this.totalPaginas || 1;
              }
              this.atualizarPaginacao();
            }
          }
        }
      ]
    });
    await alert.present();
  }

  async limparHistorico() {
    const alert = await this.alertController.create({
      header: 'Confirmar exclusão',
      message: 'Deseja limpar todo o histórico de buscas?',
      buttons: [
        {
          text: 'Cancelar',
          role: 'cancel'
        },
        {
          text: 'Limpar',
          handler: () => {
            this.historicoService.limparHistorico();
            this.historicoCidades = [];
            this.cidadesPaginadas = [];
            this.paginaAtual = 1;
            this.totalPaginas = 1;
          }
        }
      ]
    });
    await alert.present();
  }
}
<ion-header [translucent]="true">
  <ion-toolbar color="primary">
    <ion-buttons slot="start">
      <ion-back-button defaultHref="/home"></ion-back-button>
    </ion-buttons>
    <ion-title>Histórico de Buscas</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="ion-padding">
  <!-- Lista de cidades pesquisadas -->
  <ion-list *ngIf="historicoCidades.length > 0">
    <ion-item-sliding *ngFor="let cidade of cidadesPaginadas">
      <ion-item (click)="verDetalhes(cidade)" button>
        <ion-label>{{ cidade }}</ion-label>
        <ion-icon name="chevron-forward" slot="end"></ion-icon>
      </ion-item>
      <ion-item-options side="end">
        <ion-item-option color="danger" (click)="removerCidade(cidade)">
          <ion-icon name="trash" slot="icon-only"></ion-icon>
        </ion-item-option>
      </ion-item-options>
    </ion-item-sliding>
  </ion-list>

  <!-- Mensagem quando não há histórico -->
  <div class="empty-state" *ngIf="historicoCidades.length === 0">
    <ion-text color="medium">
      <h2>Nenhuma cidade no histórico</h2>
      <p>As cidades pesquisadas aparecerão aqui</p>
    </ion-text>
  </div>

  <!-- Paginação -->
  <ion-grid *ngIf="historicoCidades.length > itensPorPagina">
    <ion-row class="ion-justify-content-center">
      <ion-col size="auto">
        <ion-button fill="clear" [disabled]="paginaAtual === 1" (click)="mudarPagina(paginaAtual - 1)">
          <ion-icon name="chevron-back"></ion-icon>
        </ion-button>
        <ion-button fill="clear" disabled>
          {{ paginaAtual }} / {{ totalPaginas }}
        </ion-button>
        <ion-button fill="clear" [disabled]="paginaAtual === totalPaginas" (click)="mudarPagina(paginaAtual + 1)">
          <ion-icon name="chevron-forward"></ion-icon>
        </ion-button>
      </ion-col>
    </ion-row>
  </ion-grid>

  <!-- Botão para limpar histórico -->
  <ion-fab vertical="bottom" horizontal="end" slot="fixed" *ngIf="historicoCidades.length > 0">
    <ion-fab-button color="danger" (click)="limparHistorico()">
      <ion-icon name="trash"></ion-icon>
    </ion-fab-button>
  </ion-fab>
</ion-content>
