import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'temperatura',
  standalone: true
})
export class TemperaturaPipe implements PipeTransform {
  /**
   * Transforma a temperatura de Kelvin para Celsius
   * @param value Temperatura em Kelvin
   * @returns Temperatura em Celsius formatada com símbolo °C
   */
  transform(value: number): string {
    if (!value || isNaN(value)) {
      return 'N/A';
    }
    // Conversão de Kelvin para Celsius (K - 273.15)
    const celsius = Math.round(value - 273.15);
    return `${celsius}°C`;
  }
}
