import { Directive, ElementRef, HostBinding, HostListener } from '@angular/core';

@Directive({
  selector: '[appHighlight]',
  standalone: true
})
export class HighlightDirective {
  // Cores possíveis para o highlight
  private cores = [
    '#1976d2',  // azul
    '#388e3c',  // verde
    '#e64a19',  // laranja
    '#7b1fa2',  // roxo
    '#c2185b'   // rosa
  ];

  // Binding para a cor do texto
  @HostBinding('style.color') corTexto: string = '';

  // Binding para a cor da borda
  @HostBinding('style.borderColor') corBorda: string = '';

  // Bindings para efeitos visuais
  @HostBinding('style.border') border: string = '2px solid transparent';
  @HostBinding('style.borderRadius') borderRadius: string = '8px';
  @HostBinding('style.padding') padding: string = '10px';
  @HostBinding('style.transition') transition: string = 'all 0.3s ease';

  constructor(private el: ElementRef) {}

  /**
   * Listener para quando o mouse entra no elemento
   */
  @HostListener('mouseenter') onMouseEnter() {
    // Seleciona uma cor aleatória do array de cores
    const corAleatoria = this.cores[Math.floor(Math.random() * this.cores.length)];
    this.corTexto = corAleatoria;
    this.corBorda = corAleatoria;
    this.el.nativeElement.style.transform = 'scale(1.02)';
    this.el.nativeElement.style.boxShadow = '0 4px 8px rgba(0,0,0,0.1)';
  }

  /**
   * Listener para quando o mouse sai do elemento
   */
  @HostListener('mouseleave') onMouseLeave() {
    this.corTexto = '';
    this.corBorda = '';
    this.el.nativeElement.style.transform = 'scale(1)';
    this.el.nativeElement.style.boxShadow = 'none';
  }

  /**
   * Listener para clique no elemento
   */
  @HostListener('click') onClick() {
    const corAleatoria = this.cores[Math.floor(Math.random() * this.cores.length)];
    this.el.nativeElement.style.backgroundColor = `${corAleatoria}22`; // Cor com baixa opacidade
    setTimeout(() => {
      this.el.nativeElement.style.backgroundColor = 'transparent';
    }, 300);
  }
}
