import { Component, OnInit } from '@angular/core';
import { ClimaService } from '../services/clima.service';
import { HistoricoService } from '../services/historico.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
  standalone: false
})
export class HomePage implements OnInit {
  // Dados do clima atual
  climaAtual: any = null;
  // Previsão para 5 dias
  previsao5Dias: any[] = [];
  // Cidade pesquisada
  cidade: string = '';
  // Controle de erro
  erro: string = '';
  // Controle de loading
  carregando: boolean = false;

  constructor(
    private climaService: ClimaService,
    private historicoService: HistoricoService,
    private router: Router
  ) {}

  ngOnInit() {
    // Carrega clima de uma cidade padrão ao iniciar
    this.buscarClima('São Paulo');
  }

  /**
   * Busca os dados do clima para a cidade especificada
   */
  buscarClima(cidade: string) {
    if (!cidade) {
      this.erro = 'Por favor, digite o nome de uma cidade';
      return;
    }

    this.carregando = true;
    this.erro = '';
    this.historicoService.adicionarCidade(cidade);

    // Busca clima atual
    this.climaService.getClimaAtual(cidade).subscribe({
      next: (data) => {
        this.climaAtual = data;
        this.cidade = cidade;
        // Após sucesso, busca previsão para 5 dias
        this.buscarPrevisao5Dias(cidade);
      },
      error: (error) => {
        console.error('Erro ao buscar clima:', error);
        this.erro = 'Não foi possível encontrar o clima para esta cidade';
        this.carregando = false;
      }
    });
  }

  /**
   * Busca a previsão do tempo para 5 dias
   */
  private buscarPrevisao5Dias(cidade: string) {
    this.climaService.getPrevisao5Dias(cidade).subscribe({
      next: (data) => {
        // Filtra para pegar apenas uma previsão por dia
        this.previsao5Dias = data.list.filter((previsao: any, index: number) => index % 8 === 0);
        this.carregando = false;
      },
      error: (error) => {
        console.error('Erro ao buscar previsão:', error);
        this.carregando = false;
      }
    });
  }

  /**
   * Navega para a página de detalhes do clima
   */
  verDetalhes() {
    if (this.cidade) {
      const cidadeEncoded = encodeURIComponent(this.cidade);
      this.router.navigate(['/detalhes-clima', cidadeEncoded]);
    }
  }

  verHistorico() {
    this.router.navigate(['/historico-clima']);
  }
}
<ion-header [translucent]="true">
  <ion-toolbar color="primary">
    <ion-title>
      ClimaJá
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true" class="ion-padding">
  <!-- Cabeçalho com barra de pesquisa -->
  <div class="search-container">
    <ion-item lines="none" class="search-box" appHighlight>
      <ion-input 
        [(ngModel)]="cidade" 
        placeholder="Digite o nome da cidade"
        (keyup.enter)="buscarClima(cidade)">
      </ion-input>
      <ion-button slot="end" (click)="buscarClima(cidade)" [disabled]="carregando">
        <ion-icon name="search-outline"></ion-icon>
      </ion-button>
    </ion-item>
  </div>

  <!-- Loading indicator -->
  <div class="loading-container" *ngIf="carregando">
    <ion-spinner name="circular"></ion-spinner>
    <p>Buscando dados do clima...</p>
  </div>

  <!-- Mensagem de erro -->
  <ion-text color="danger" *ngIf="erro">
    <p class="error-message">{{ erro }}</p>
  </ion-text>

  <!-- Card do clima atual -->
  <ion-card *ngIf="climaAtual && !carregando" class="clima-card" appHighlight>
    <ion-card-header>
      <ion-card-subtitle>Clima Atual em</ion-card-subtitle>
      <ion-card-title>{{ climaAtual.name }}</ion-card-title>
    </ion-card-header>

    <ion-card-content>
      <div class="clima-info">
        <div class="temperatura-principal">
          <h1>{{ climaAtual.main.temp | temperatura }}</h1>
          <p>{{ climaAtual.weather[0].description | titlecase }}</p>
        </div>

        <div class="detalhes-grid">
          <div class="detalhe-item">
            <ion-icon name="thermometer-outline"></ion-icon>
            <span>Sensação</span>
            <strong>{{ climaAtual.main.feels_like | temperatura }}</strong>
          </div>
          <div class="detalhe-item">
            <ion-icon name="water-outline"></ion-icon>
            <span>Umidade</span>
            <strong>{{ climaAtual.main.humidity }}%</strong>
          </div>
          <div class="detalhe-item">
            <ion-icon name="speedometer-outline"></ion-icon>
            <span>Pressão</span>
            <strong>{{ climaAtual.main.pressure }} hPa</strong>
          </div>
          <div class="detalhe-item">
            <ion-icon name="wind-outline"></ion-icon>
            <span>Vento</span>
            <strong>{{ climaAtual.wind.speed }} m/s</strong>
          </div>
        </div>
      </div>
    </ion-card-content>
  </ion-card>

  <!-- Previsão para 5 dias -->
  <div class="previsao-container" *ngIf="previsao5Dias.length > 0 && !carregando">
    <h2>Previsão para 5 dias</h2>
    <ion-grid>
      <ion-row>
        <ion-col size="12" *ngFor="let previsao of previsao5Dias">
          <ion-card class="previsao-card" appHighlight>
            <ion-card-content>
              <div class="previsao-info">
                <div class="data">
                  {{ previsao.dt * 1000 | date:'EEE, dd/MM' }}
                </div>
                <div class="temperatura">
                  {{ previsao.main.temp | temperatura }}
                </div>
                <div class="descricao">
                  {{ previsao.weather[0].description }}
                </div>
              </div>
            </ion-card-content>
          </ion-card>
        </ion-col>
      </ion-row>
    </ion-grid>
  </div>

  <!-- Botões de ação -->
  <div class="botoes-acao">
    <ion-button 
      expand="block" 
      color="primary" 
      class="ver-detalhes-btn"
      *ngIf="climaAtual"
      (click)="verDetalhes()">
      Ver Mais Detalhes
    </ion-button>

    <ion-button 
      expand="block" 
      color="secondary" 
      class="historico-btn"
      (click)="verHistorico()">
      <ion-icon name="time-outline" slot="start"></ion-icon>
      Histórico de Buscas
    </ion-button>
  </div>
</ion-content>
